{{setVar 'x' (body 'x')}}
{{setVar 'y' (body 'y')}}
{{setVar 'z' (body 'z')}}
{{setVar 'F' (body 'feedrate')}}

{{#if (eq (bodyRaw 'units') 'milimeters')}}
  {{setVar 'units' 'G21'}}
{{else}}
  {{setVar 'units' 'G20'}}
{{/if}}

{{#if (eq (bodyRaw 'mode') 'distance_incremental')}}
  {{setVar 'mode' 'G91'}}
{{else}}
  {{setVar 'mode' 'G90'}}
{{/if}}

{
  "success": "El comando fue enviado para su ejecución",
  "command": "$J={{@mode}} {{@units}} X{{@x}} Y{{@y}} Z{{@z}} F{{@F}}"
}
